mapper.select:

select no, title, contents, hit, date_format(reg_date,'%Y%m%d %H:%i:%S'), group_no, order_no, depth, user_no 
from board 
where no=#{no }

mapper.update:
update borad 
set title=#{title },contents=#{contents }
where no=#{no }

mapper.select:
select title,contents,user_no 
from board 
where no=#{no}

select title, contents 
from borad 
where no=#{no}
